// src/pages/ActivityDetail.js
import React, { useEffect, useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import PageHeader from '../components/layout/PageHeader';
import { featuredActivities } from '../data/activities';
import '../styles/pages/ActivityDetail.css';

const ActivityDetail = () => {
  const { id } = useParams();
  const [activity, setActivity] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const foundActivity = featuredActivities.find(act => act.id === id);
    if (foundActivity) {
      setActivity(foundActivity);
    }
    setLoading(false);
  }, [id]);

  if (loading) {
    return (
      <div className="loading-container">
        <div className="loading-spinner"></div>
        <p>Activiteit laden...</p>
      </div>
    );
  }

  if (!activity) {
    return (
      <>
        <PageHeader 
          title="Activiteit niet gevonden" 
          subtitle="De gezochte activiteit bestaat niet"
        />
        <div className="container not-found">
          <p>De activiteit die u zoekt kon niet worden gevonden.</p>
          <Link to="/activiteiten" className="back-button">
            Terug naar activiteiten
          </Link>
        </div>
      </>
    );
  }

  return (
    <>
      <PageHeader 
        title={activity.title} 
        subtitle={`${activity.location} | ${activity.period}`}
      />

      <section className="activity-detail-section">
        <div className="container">
          <div className="activity-detail-header">
            <div className="activity-detail-image">
              <img src={activity.image} alt={activity.title} />
            </div>

            <div className="activity-detail-tags">
              {activity.tags.map((tag, index) => (
                <span key={index} className="detail-tag">{tag}</span>
              ))}
            </div>
          </div>

          <div className="activity-detail-content">
            <div className="detail-columns">
              <div className="detail-column">
                <div className="detail-box">
                  <h3>Doelstellingen van de activiteit</h3>
                  <ul>
                    {activity.content.objectives.map((obj, index) => (
                      <li key={index}>{obj}</li>
                    ))}
                  </ul>
                </div>
              </div>

              <div className="detail-column">
                <div className="detail-box">
                  <h3>Eigen doelstellingen</h3>
                  <ul>
                    {activity.content.personalObjectives.map((obj, index) => (
                      <li key={index}>{obj}</li>
                    ))}
                  </ul>
                </div>
              </div>
            </div>

            {activity.content.team && (
              <div className="detail-box team-box">
                <h3>Team samenstelling</h3>
                <p>{activity.content.team}</p>
              </div>
            )}

            <div className="detail-box">
              <h3>Kern</h3>
              <div className="content-text">
                {activity.content.description.split('\n\n').map((paragraph, index) => (
                  <p key={index}>{paragraph}</p>
                ))}
              </div>
            </div>

            <div className="detail-box">
              <h3>Reflectie</h3>
              <div className="content-text">
                {activity.content.reflection.split('\n\n').map((paragraph, index) => (
                  <p key={index}>{paragraph}</p>
                ))}
              </div>
            </div>

            <div className="activity-detail-navigation">
              <Link to="/activiteiten" className="back-button">
                Terug naar activiteiten
              </Link>
            </div>
          </div>
        </div>
      </section>
    </>
  );
};

export default ActivityDetail;
