import React from 'react';
import { useParams } from 'react-router-dom';
import { requiredActivities, extraActivities } from '../../data/activities';
import '../../styles/components/ActivityDetail.css';

// Import banner images
import hackTheFutureLogo from '../../images/Hack_The_Future_Logo.jpg';
import esnBelgiumLogo from '../../images/ESN_Belgium_Logo.png';
import insticcLogo from '../../images/INSTICC_Logo.png';

const activityBanners = {
  'HackTheFuture': hackTheFutureLogo,
  'Hack The Future (Cronos Groep)': hackTheFutureLogo,
  'ESN Belgium': esnBelgiumLogo,
  'IT Manager (ESN Belgium)': esnBelgiumLogo,
  'Buitenlandse Stage': insticcLogo,
  'Buitenlandse Stage - INSTICC, Setúbal Portugal': insticcLogo
};

const ActivityDetail = () => {
  const { id, type } = useParams();

  // Get all activities or filter by type if specified
  const allActivities = type === 'required' 
    ? requiredActivities 
    : type === 'extra' 
      ? extraActivities 
      : [...requiredActivities, ...extraActivities];

  const activity = allActivities[id];

  if (!activity) {
    return <div className="error-message">Activity not found</div>;
  }

  // Find banner image for this activity
  const bannerImage = activityBanners[activity.title];

  return (
    <div className="activity-detail-container">
      <div className="activity-header">
        <h2>{activity.title}</h2>
        <p className="activity-location-date">{activity.location} | {activity.date || activity.period}</p>
      </div>

      {/* Add banner image */}
      {bannerImage && (
        <div className="activity-detail-banner">
          <img src={bannerImage} alt={`${activity.title} banner`} />
        </div>
      )}

      <div className="activity-content">
        <div className="activity-meta">
          <div className="meta-item">
            <h4>Periode:</h4>
            <p>{activity.date || activity.period}</p>
          </div>
          <div className="meta-item">
            <h4>Locatie:</h4>
            <p>{activity.location}</p>
          </div>
          {activity.domain && (
            <div className="meta-item">
              <h4>Domein:</h4>
              <p>{activity.domain}</p>
            </div>
          )}
        </div>

        <div className="activity-description">
          <p>{activity.detailedDescription || activity.description}</p>
        </div>

        {activity.skills && (
          <div className="activity-skills">
            {activity.skills.map((skill, index) => (
              <span key={index} className="skill-tag">{skill}</span>
            ))}
          </div>
        )}

        {activity.learnings && (
          <div className="activity-learnings">
            <h3>Wat ik heb geleerd</h3>
            <ul>
              {activity.learnings.map((learning, index) => (
                <li key={index}>{learning}</li>
              ))}
            </ul>
          </div>
        )}

        {activity.reflection && (
          <div className="activity-reflection">
            <h3>Reflectie</h3>
            <p>{activity.reflection}</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default ActivityDetail;
