// src/components/activities/ActivityDetail.js
import React from 'react';
import { useParams } from 'react-router-dom';
import { allActivities } from '../data/activities'; // Pas dit pad aan naar waar je activiteiten zijn opgeslagen
import '../styles/pages/ActivityDetail.css';

const ActivityDetail = () => {
  // Haal het ID van de activiteit uit de URL parameters
  const { id } = useParams();

  // Zoek de activiteit op basis van het ID
  const activity = allActivities.find(act => act.id.toString() === id);

  // Helper functie om het juiste logo te bepalen op basis van organisatie
  const getOrganizationLogo = (organization) => {
    const orgLogoMap = {
      'ESN Belgium': '/images/ESN_Belgium_Logo.png',
      'ESN Hasselt': '/images/ESN_Belgium_Logo.png',
      'Hack The Future': '/images/Hack_The_Future_Logo.jpg',
      'INSTICC': '/images/INSTICC_Logo.png',
      'Cronos Groep': '/images/Cronos_Groep_Logo.png',
    };

    return orgLogoMap[organization] || null;
  };

  if (!activity) {
    return <div className="loading">Activiteit niet gevonden...</div>;
  }

  return (
    <div className="activity-detail">
      <div className="activity-header">
        <div className="activity-header-content container">
          <h1>{activity.title}</h1>

          {/* Add the banner image here */}
          {getOrganizationLogo(activity.organization) && (
            <div className="activity-banner">
              <img 
                src={getOrganizationLogo(activity.organization)} 
                alt={`${activity.organization} Banner`} 
              />
            </div>
          )}

          <div className="activity-meta">
            <div className="activity-organization-period">
              <p className="activity-organization">{activity.organization}</p>
              <p className="activity-period">{activity.period}</p>
            </div>
            {getOrganizationLogo(activity.organization) && (
              <div className="organization-logo-container">
                <img 
                  src={getOrganizationLogo(activity.organization)} 
                  alt={`${activity.organization} Logo`} 
                  className="organization-logo"
                />
              </div>
            )}
          </div>
        </div>
      </div>

      <div className="activity-content container">
        <div className="activity-main">
          <div className="activity-description">
            <h2>Beschrijving</h2>
            <div dangerouslySetInnerHTML={{ __html: activity.description }} />
          </div>

          {activity.achievements && activity.achievements.length > 0 && (
            <div className="activity-achievements">
              <h2>Prestaties</h2>
              <ul>
                {activity.achievements.map((achievement, index) => (
                  <li key={index}>{achievement}</li>
                ))}
              </ul>
            </div>
          )}
        </div>

        <div className="activity-sidebar">
          {activity.skills && activity.skills.length > 0 && (
            <div className="activity-skills-box">
              <h3>Ontwikkelde Vaardigheden</h3>
              <div className="skills-list">
                {activity.skills.map((skill, index) => (
                  <span key={index} className="skill-tag">{skill}</span>
                ))}
              </div>
            </div>
          )}

          {activity.tools && activity.tools.length > 0 && (
            <div className="activity-tools-box">
              <h3>Gebruikte Tools</h3>
              <ul>
                {activity.tools.map((tool, index) => (
                  <li key={index}>{tool}</li>
                ))}
              </ul>
            </div>
          )}

          {activity.links && activity.links.length > 0 && (
            <div className="activity-links-box">
              <h3>Relevante Links</h3>
              <ul>
                {activity.links.map((link, index) => (
                  <li key={index}>
                    <a href={link.url} target="_blank" rel="noopener noreferrer">
                      {link.title}
                    </a>
                  </li>
                ))}
              </ul>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default ActivityDetail;
